// Key types at a glance:

interface Page<T> {
  items: T[];
  nextCursor?: string;
}

type Cursor = string & { readonly __brand: "Cursor" };

type FetchError =
  | { kind: "NetworkError"; message: string }
  | { kind: "ParseError"; message: string; raw: string }
  | { kind: "RateLimitError"; retryAfterMs: number };

type Result<T, E> =
  | { ok: true;  value: T }
  | { ok: false; error: E };

type PageFetcher<T> = (
  cursor: Cursor | undefined
) => Promise<Result<Page<T>, FetchError>>;

