// Key types at a glance

type Milliseconds = number & { readonly __brand: "Milliseconds" };

interface PipelineContext<TBody = unknown> {
  readonly requestId: string;
  url: string;
  method: "GET" | "POST" | "PUT" | "PATCH" | "DELETE";
