  totalItems:     number;
  totalStock:     number;
  totalValueUsd:  number;
  cheapestSku:    Sku;
  mostStockedSku: Sku;
}

type InventorySummary = Record<string, CategorySummary>;

function aggregateInventory(items: InventoryItem[]): InventorySummary { ... }
function topCategories(summary: InventorySummary, n: number): string[] { ... }

// Key types and main function signature

declare const __brand: unique symbol;
type Brand<B> = { readonly [__brand]: B };

type Cursor      = string & Brand<"Cursor">;
type EndpointPath = string & Brand<"EndpointPath">;

type PageResponse<T> = {
  items:      T[];
  nextCursor: Cursor | undefined;
  pageIndex:  number;
};

type FetchResult<T> =
  | { ok: true;  data: T }
  | { ok: false; error: PageError };

type AccumulatedResult<T> = /* discriminated by `ok` — you define it */;

type PageFetcher<T> =
  (req: PageRequest) => Promise<FetchResult<PageResponse<T>>>;

// ── Core entry point ──────────────────────────────────────────
async function fetchAllPages<T>(
  endpoint: EndpointPath,
  fetcher:  PageFetcher<T>,
  options?: FetchAllOptions
): Promise<AccumulatedResult<T>>

/** All supported event kinds */
type EventKind = "deploy" | "error" | "scale" | "alert";

interface AlertEvent {
  kind: "alert";
  timestamp: number;
  alertName: string;
  severity: "low" | "medium" | "high" | "critical";
}

type AppEvent = DeployEvent | ErrorEvent | ScaleEvent | AlertEvent;

/** Mapped over EventKind — adding a new kind extends this automatically */
type EventSummary = {
  [K in EventKind]: {
    count: number;
    latestTimestamp: number | null;
  };
};

/** Parse one unknown blob → typed AppEvent or null if invalid */
function parseEvent(raw: unknown): AppEvent | null { ... }

/** Parse an array of unknown blobs, silently dropping invalid entries */
function parseEventLog(raw: unknown): AppEvent[] { ... }

/** Aggregate parsed events into per-kind counts + latest timestamps */
function summariseEvents(events: AppEvent[]): EventSummary { ... }

export type CheckoutState =
  | IdleState
  | ValidatingState
  | PaymentState
  | ConfirmedState
  | FailedState;

// Each event variant carries only the payload its transition needs:
export type CheckoutEvent = never; // TODO: your discriminated union here

// TransitionMap maps every event `type` to its allowed source states
// and a pure reduce function — narrowed at the type level:
export type TransitionMap = {
  [E in CheckoutEvent as E["type"]]: {
    from: ReadonlyArray<CheckoutState["kind"]>;
    reduce: (state: /* narrowed */ CheckoutState, event: E) => CheckoutState;
  };
};

export class CheckoutMachine {
  constructor(initialCart: CartItem[]);
  getState(): CheckoutState;
  send(event: CheckoutEvent): CheckoutState;
  subscribe(fn: Subscriber): () => void;
  reset(newCart?: CartItem[]): CheckoutState;
}
